def draw(dots):
    plt.figure(figsize=(10,10))
    plt.text(loc[0][1] + 1, loc[0][2] + 1, '配送中心', color='red', size=10)
    plt.plot(loc[0][1], loc[0][2], '.',markersize=8, color='red')
    colors = ['#0085c3', 'orange',  'green', 'blue', 'darkgray', 'deeppink', 'black', 'brown', 'purple','lightgreen','plum','royalblue','yellow','slategrey','indigo']
    for i in range(len(dots)):
        for j in range(len(dots[i])):
            if loc[dots[i][j]][3] == 0:
                plt.plot(loc[dots[i][j]][1], loc[dots[i][j]][2], '^',markersize=4, color=colors[i%len(colors)])
            elif loc[dots[i][j]][4] == 0:
                plt.plot(loc[dots[i][j]][1], loc[dots[i][j]][2], 's', markersize=4, color=colors[i % len(colors)])
            else:
                plt.plot(loc[dots[i][j]][1], loc[dots[i][j]][2], '.', markersize=8, color=colors[i % len(colors)])
    plt.show()


